public class Car{

    String model;

    Engine myEngine;
    Wheel myWheel;
    float fuelTank;
    int passengerCapacity;

    public Car(String model){
        this.model = model;
        Engine myEngine = new Engine(model);
        Wheel myWheel = new Wheel(model);
        passengerCapacity = CarBase.getPassengerCapacity(model);
    }

    public void startEngine() throws CheckEngineException{
        myEngine.callStarter();
    }

    public void rotateWheel(float angle){
        if (angle > 0) {
            myWheel.rotateClockwise(Math.abs(angle));
        } else {
            myWheel.rotateCounterClockwise(Math.abs(angle));
        }
    }

}
