import java.lang.Runnable;

public class Test {

    public static void proceess(int register) {

        final int counter = 0;

        checkAndStart((register > 10), new Runnable() {
            public void run() {
                counter++;
            }
        });
    }

    public static void checkAndStart(boolean flag, Runnable runnable){
        if(flag) runnable.run();
    }

}
