class DeleteDemo{

        private class Person{
        private String name;
        private int age;
        private String cellphone;

        Person(String name, int age, String cellphone){
            this.name = name;
            this.age = age;
            this.cellphone = cellphone;
        }

        public String getCellphone() {
            return cellphone;
        }

        public void setCellphone(String cellphone) {
            this.cellphone = cellphone;
        }
    }
}
