    func firstWarning() {
        var unused1 = 0
        var unused2 = 1
    }

    func secondWarning() {
        var unused3 = 1
        print(__LINE__)
    }

    func typoInspection() {
        var incorrctlySpelled = "incorrctlySpeled"
