MouseBlock.CopyText:
import Foundation

public func fib(n: Int) -> Int {
    var a = 1;
    var b = 1;

    var tmp = 0

    if n < 2 {
        return 1
    }

    for _ in 0...n - 2 {
        tmp = b;
        b = a;
        a = a + tmp;
    }

    return a;
}

for i in 0...100 {
    print(fib(n: i))
}
